If(ThisItem.IsSelected,ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Button Fill").'Default Value' )
//ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Button Fill").'Default Value' )//ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Button Fill").'Default Value' )
,ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Text Background Color").'Default Value') //ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Text Primary Color").'Default Value') //_VarGblAppColors.textbgbutton
)

If(
    ThisItem.IsSelected,
    ColorValue(
        LookUp(
            c_EnvironmentVariable,
            'Display Name' = "Button Fill"
        ).'Default Value'
    ),//ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Button Fill").'Default Value' ),
    ColorValue(LookUp(c_EnvironmentVariable,'Display Name'="Text Background Color").'Default Value' )
)

Table(
    {
        TabID: 1,
        Title: "Invoice"
    },
    {
        TabID: 2,
        Title: "Invoice Line"
    }
)

Table(
    {
        Title: "Invoice Id:",
        Data: varInvoiceData.'Invoice Id'},
    {
        Title: "Order No:",
        Data: varInvoiceData.'Order No.'.'Order ID'
    },
    {
        Title: "Date:",
        Data: Text(
            varInvoiceData.'Created On',
            "dd-mm-yyyy hh:mm AM/PM"
        )
    },
    {
        Title: "Status:",
        Data: Text(varInvoiceData.'Invoice Status')
    },
   
    {
        Title: "Party:",
        Data: varInvoiceData.Party.'Customer Name'
    },
   
   
    {
        Title: "CGST:",
        Data: Concatenate(
            "₹",
            Text(
                varInvoiceData.CGST,
                "[$-hi-IN]##,##,##,###",
                "hi-IN"
            )
        )
    },
    {
        Title: "SGST:",
        Data: Concatenate(
            "₹",
            Text(
                varInvoiceData.SGST,
                "[$-hi-IN]##,##,##,###",
                "hi-IN"
            )
        )
    },
    {
        Title: "IGST:",
        Data: Concatenate(
            "₹",
            Text(
                varInvoiceData.IGST,
                "[$-hi-IN]##,##,##,###",
                "hi-IN"
            )
        )
    },
 
    {
        Title: "Total Discount:",
        Data: Concatenate(
            "₹",
            Text(
                varInvoiceData.'Total Discount',
                "[$-hi-IN]##,##,##,###",
                "hi-IN"
            )
        )
    },
     {
        Title: "Total Price:",
        Data: Concatenate(
            "₹",          
            Text(
                varInvoiceData.'Total Amount',
                "[$-hi-IN]##,##,##,###",
                "hi-IN"
            )       
        )
    }
)
