ClearCollect(
    colOrderLine,
    Filter(
        OfflineOrderLineCollection,
        'Order Header'.'Order Header' = ThisItem.zx_orderheaderid
    )
);
/*Set(
    varActivated,
    ThisItem.'Order From'.'Credit Activated'
);*/
Set(
        varSelectedRetailerAttachedDistributor,
        LookUp(
            OfflinePartiesCollection,
            'Customer Code' = ThisItem.zx_OrderFrom.'Customer Code'
        ).'Attached Distributor'.'Customer Name'
    );
Set(
        varpaymentterm,
        ThisItem.zx_PaymentTerms.Name
    );
Set(
    varRemainingCreditLimit,
    Text(
        RoundUp(
            Value(ThisItem.zx_OrderFrom.'Remaining Credit Limit'),
            2
        ),
        "[$-en-US]#,###.00"
    )
);
If(
    ThisItem.zx_ordertype = 'Order Type Choice'.Primary,
    Set(
        primarydata,
        ThisItem
    );
    ForAll(
        colOrderLine As oneline,
        If(
            (CountRows(colPrimaryCart) = 0) || IsBlank(
                LookUp(
                    colPrimaryCart,
                    productSKU = oneline.'Product SKU'.'Product SKU'
                )
            ),
            Collect(
                colPrimaryCart,
                {
                    productName: oneline.'Product SKU'.'Item Name',
                    product: oneline.Product.Product,
                    baseUnitPrice: oneline.'Unit Price',
                    productSKU: oneline.'Product SKU'.'Product SKU',
                    quantity: Text(oneline.Quantity),
                    Variant: oneline.Variant.Name,
                    discount: oneline.'Discount %',
                    cgstAmount: ThisItem.zx_cgst,
                    sgstAmount: ThisItem.zx_sgst,
                    igstAmount: ThisItem.zx_igst,
                    cessAmount: ThisItem.zx_cess
                }
            )
        );
    );
    Navigate(
        'Primary Cart Screen',
        ScreenTransition.CoverRight,
        {varActivated: ThisItem.zx_OrderFrom.'Credit Activated',varParty:
    ThisItem.zx_OrderFrom.'Customer Name'
}
    ),
    ThisItem.zx_ordertype = 'Order Type Choice'.Secondary,
    Set(
        secondarydata,
        ThisItem
    );
    ForAll(
        colOrderLine As oneline,
        If(
            (CountRows(colSecondaryCart) = 0) || IsBlank(
                LookUp(
                    colSecondaryCart,
                    productSKU = oneline.'Product SKU'.'Product SKU'
                )
            ),
            Collect(
                colSecondaryCart,
                {
                    productName: oneline.'Product SKU'.'Item Name',
                    product: oneline.Product.Product,
                    baseUnitPrice: oneline.'Unit Price',
                    productSKU: oneline.'Product SKU'.'Product SKU',
                    quantity: Text(oneline.Quantity),
                    Variant: oneline.Variant.Name,
                    discount: oneline.'Discount %',
                    cgstAmount: ThisItem.zx_cgst,
                    sgstAmount: ThisItem.zx_sgst,
                    igstAmount: ThisItem.zx_igst,
                    cessAmount: ThisItem.zx_cess
                }
            )
        );
    );
    Navigate(
        'Secondary Cart Screen',
        ScreenTransition.CoverRight,
        {varActivated: ThisItem.zx_OrderFrom.'Credit Activated',varParty:
    ThisItem.zx_OrderFrom.'Customer Name'
}
    )
);
/*Set(
    varParty,
    ThisItem.zx_OrderFrom.'Customer Name'
);*/
Set(
    varorderselectedId,
    1
)

Navigate(
    'order detail screen',
    ScreenTransition.CoverRight,
    {varOrderData: ThisItem}
);
