    'order detail screen',
    ScreenTransition.CoverRight,
    {varOrderData: ThisItem}
);
//Set(varOrderData, ThisItem);

If(
    varfiltergallery = true,
    If(
        'Sort gallery_4'.Selected.Title = "Newest",
        Sort(
            If(
                'Order List'.Type = "Primary",
                If(
                    IsToday(DatePicker1.SelectedDate),
                    Filter(
                        OfflineOrderHeaderCollection,
                        'Order Type' = 'Order Type Choice'.Primary && 'Created On' <= Today()+1 && 'Created On' > DateAdd(
                            Today(),
                            -7
                        )
                    ),
                    Filter(
                        OfflineOrderHeaderCollection,
                        'Order Type' = 'Order Type Choice'.Primary && Text(
                            'Created On',
                            "dd/mm/yyyy"
                        ) = Text(
                            DatePicker1.SelectedDate,
                            "dd/mm/yyyy"
                        )
                    )
                ),
                'Order List'.Type = "Secondary",
                If(
                    IsToday(DatePicker1.SelectedDate),
                    Filter(
                        OfflineOrderHeaderCollection,
                        'Order Type' = 'Order Type Choice'.Secondary && 'Created On' <= Today()+1 && 'Created On' > DateAdd(
                            Today(),
                            -7
                        )
                    ),
                    Filter(
                        OfflineOrderHeaderCollection,
                        'Order Type' = 'Order Type Choice'.Secondary && Text(
                            'Created On',
                            "dd/mm/yyyy"
                        ) = Text(
                            DatePicker1.SelectedDate,
                            "dd/mm/yyyy"
                        )
                    )
                )
            ),
            'Created On',
            SortOrder.Descending
        ),
        // Sort with Oldest-------------------------------------------------
        'Sort gallery_4'.Selected.Title = "Oldest",
        Sort(
            If(
                'Order List'.Type = "Primary",
                Filter(
                    OfflineOrderHeaderCollection,
                    'Order Type' = 'Order Type Choice'.Primary && Text(
                        'Created On',
                        "dd/mm/yyyy"
                    ) = Text(
                        DatePicker1.SelectedDate,
                        "dd/mm/yyyy"
                    )
                ),
                'Order List'.Type = "Secondary",
                Filter(
                    OfflineOrderHeaderCollection,
                    'Order Type' = 'Order Type Choice'.Secondary && Text(
                        'Created On',
                        "dd/mm/yyyy"
                    ) = Text(
                        DatePicker1.SelectedDate,
                        "dd/mm/yyyy"
                    )
                )
            ),
            'Created On',
            SortOrder.Ascending
        ),
        // Sort with Higest to Lowest-------------------------------------------------
        'Sort gallery_4'.Selected.Title = "Highest to Lowest",
        Sort(
            If(
                'Order List'.Type = "Primary",
                Filter(
                    OfflineOrderHeaderCollection,
                    'Order Type' = 'Order Type Choice'.Primary && Text(
                        'Created On',
                        "dd/mm/yyyy"
                    ) = Text(
                        DatePicker1.SelectedDate,
                        "dd/mm/yyyy"
                    )
                ),
                'Order List'.Type = "Secondary",
                Filter(
                    OfflineOrderHeaderCollection,
                    'Order Type' = 'Order Type Choice'.Secondary && Text(
                        'Created On',
                        "dd/mm/yyyy"
                    ) = Text(
                        DatePicker1.SelectedDate,
                        "dd/mm/yyyy"
                    )
                )
            ),
            'Total Bill Amount',
            SortOrder.Descending
        ),
        // Sort with Lowest to Highest-------------------------------------------------
        'Sort gallery_4'.Selected.Title = "Lowest to Highest",
        Sort(
            If(
                'Order List'.Type = "Primary",
                Filter(
                    OfflineOrderHeaderCollection,
                    'Order Type' = 'Order Type Choice'.Primary && Text(
                        'Created On',
                        "dd/mm/yyyy"
                    ) = Text(
                        DatePicker1.SelectedDate,
                        "dd/mm/yyyy"
                    )
                ),
                'Order List'.Type = "Secondary",
                Filter(
                    OfflineOrderHeaderCollection,
                    'Order Type' = 'Order Type Choice'.Secondary && Text(
                        'Created On',
                        "dd/mm/yyyy"
                    ) = Text(
                        DatePicker1.SelectedDate,
                        "dd/mm/yyyy"
                    )
                )
            ),
            'Total Bill Amount',
            SortOrder.Ascending
        )
    ),
        // Custom filter data --------------------------------------------------
    varfiltergallery = false,
    // Sort with Newest-------------------------------------------------
    If(
        'Sort gallery_4'.Selected.Title = "Newest",
        Sort(
            colFilterData,
            createdon,
            SortOrder.Descending
        ),
        // Sort with Oldest-------------------------------------------------
        'Sort gallery_4'.Selected.Title = "Oldest",
        Sort(
            colFilterData,
            createdon,
            SortOrder.Ascending
        ),
        // Sort with Higest to Lowest-------------------------------------------------
        'Sort gallery_4'.Selected.Title = "Highest to Lowest",
        Sort(
            colFilterData,
            zx_totalbillamount,
            SortOrder.Descending
        ),
        // Sort with Lowest to Highest-------------------------------------------------
        'Sort gallery_4'.Selected.Title = "Lowest to Highest",
        Sort(
            colFilterData,
          zx_totalbillamount,
            SortOrder.Ascending
        )
    )
)

/*["Newest",
"Highest to Lowest",
"Lowest to Highest"]*/
Table(
    {Title: "Newest",
    Icon: Icon.ArrowDown},
    {Title: "Oldest",
    Icon: Icon.ArrowUp},
    {
        Title: "Highest to Lowest",
        Icon: Icon.ArrowDown
    },
    {
        Title: "Lowest to Highest",
        Icon: Icon.ArrowUp
    }
)
