Rules.InvariantScript:
Table(
    {
        Title: "Date",
        tabid: 1
    },
    {
        Title: "Salesperson",
        tabid: 2
    },
    {
        Title: "Order From",
        tabid: 3
    },
    {
        Title: "Status",
        tabid: 4
    },
    {
        Title: "Order value",
        tabid: 5
    }
)

If(
    Gallery8_5.Selected.tabid = 1 && IsBlank(
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ).Title
    ),
    Collect(
        colFilter,
        {
            Title: Gallery8_5.Selected.Title,
            value: radioDate_5.Selected.Value
        }
    ),
    Patch(
        colFilter,
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ),
        {value: radioDate_5.Selected.Value}
    )
)

    If(Gallery8_5.Selected.tabid = 3  && IsBlank(LookUp(colFilter,Title= Gallery8_5.Selected.Title).Title),
    Collect(
        colFilter,
        {
            Title: Gallery8_5.Selected.Title,
            value: radioSalesperson_5.Selected.'Full Name'
        }
    ),
    Patch(colFilter,LookUp(colFilter,Title= Gallery8_5.Selected.Title),{value: radioSalesperson_5.Selected.'Full Name'})
)

If(
    Gallery8_5.Selected.tabid = 4 && IsBlank(
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ).Title
    ),
    Collect(
        colFilter,
        {
            Title: Gallery8_5.Selected.Title,
            value: Text(radioStatus_1.Selected.Value)
        }
    ),
     Patch(
        colFilter,
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ),
        {value: Text(radioStatus_1.Selected.Value)}
    )
   
)

If(
    Gallery8_5.Selected.tabid = 3 && IsBlank(
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ).Title
    ),
    Collect(
        colFilter,
        {
            Title: Gallery8_5.Selected.Title,
            value: radioParty_1.Selected.'Customer Name'
        }
    ),
    Patch(
        colFilter,
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ),
        {value: radioParty_1.Selected.'Customer Name'}
    )
)

If(
    Gallery8_5.Selected.tabid = 5 && IsBlank(
        LookUp(
            colFilter,
            Title = Gallery8_5.Selected.Title
        ).Title
    ),
    Collect(
        colFilter,
        {
            Title: Gallery8_5.Selected.Title,
            value: OrderValue_1.Text
        }
    ),
    Patch(
        colFilter,
        LookUp(
            colFilter,
            Title =Gallery8_5.Selected.Title
        ),
        {
            value: OrderValue_1.Text
        }
    )
)

If(
    Gallery8_5.Selected.tabid = 1 && IsBlank(
        LookUp(
            colFilter,
            Title = "Date From"
        ).Title
    ),
    Collect(
        colFilter,
        {
            Title: "Date From",
            value: Text(
                DateFrom_5.SelectedDate,
                "dd-mm-yyyy"
            )
        }
    ),
    Patch(
        colFilter,
        LookUp(
            colFilter,
            Title = "Date From"
        ),
        {
            value: Text(
                DateFrom_5.SelectedDate,
                "dd-mm-yyyy"
            )
        }
    )
)

If(
    Gallery8_5.Selected.tabid = 1 && IsBlank(
        LookUp(
            colFilter,
            Title = "Date To"
        ).Title
    ),
    Collect(
        colFilter,
        {
            Title: "Date To",
            value: Text(
                DateTo_5.SelectedDate,
                "dd-mm-yyyy"
            )
        }
    ),
    Patch(
        colFilter,
        LookUp(
            colFilter,
            Title = "Date To"
        ),
        {
            value: Text(
                DateTo_5.SelectedDate,
                "dd-mm-yyyy"
            )
        }
    )
)

Reset(DateFrom_5);
Reset(DateTo_5);
Reset(radioDate_5);
Reset(radioStatus_1);
Reset(OrderValue_1);
Reset(radioParty_1);



Clear(colFilter);
Clear(colFilterData);
Set(varCollectionEmpty,true)

/*["Newest",
"Highest to Lowest",
"Lowest to Highest"]*/
Table(
    {Title: "Newest",
    Icon: Icon.ArrowDown},
    {Title: "Oldest",
    Icon: Icon.ArrowUp},
    {
        Title: "Highest to Lowest",
        Icon: Icon.ArrowDown
    },
    {
        Title: "Lowest to Highest",
        Icon: Icon.ArrowUp
    }
)
