If(
    //Category
    !IsBlank(ComboCategory_2.Selected) && IsBlank(ComboSubCategory_2.Selected) && IsBlank(ComboProductGroup_2.Selected),
    Filter(
        Filter(
            OfflineProductSKUCollection As SkuItem,
            !IsBlank(
                LookUp(
                    OfflineProductPriceCollection,
                    'Product SKU'.'Product SKU' = SkuItem.'Product SKU' && 'Price list'.Name = varPricelist.Name
                ).'Ex-Factory'
            )
        ),
        'Product Category'.'Category Code' = ComboCategory_2.Selected.'Category Code' && ('Search Input_2'.Text in Product.'Product Name' || 'Search Input_2'.Text in 'Item Code' || 'Search Input_2'.Text in LookUp(
            OfflineVariantCollection,
            Variants = LookUp(
                OfflineProductVariantCollection,
                'Product Variants' = 'Product Variant'.'Product Variants'
            ).Variant.Variants
        ).'Variant Name')
    ),
    //Product Group
    IsBlank(ComboCategory_2.Selected) && IsBlank(ComboSubCategory_2.Selected) && !IsBlank(ComboProductGroup_2.Selected),
    Filter(
        Filter(
            OfflineProductSKUCollection As SkuItem,
            !IsBlank(
                LookUp(
                    OfflineProductPriceCollection,
                    'Product SKU'.'Product SKU' = SkuItem.'Product SKU' && 'Price list'.Name = varPricelist.Name
                ).'Ex-Factory'
            )
        ),
        'Product Group'.'Group Code' = ComboProductGroup_2.Selected.'Group Code' && ('Search Input_2'.Text in Product.'Product Name' || 'Search Input_2'.Text in 'Item Code' || 'Search Input_2'.Text in LookUp(
            OfflineVariantCollection,
            Variants = LookUp(
                OfflineProductVariantCollection,
                'Product Variants' = 'Product Variant'.'Product Variants'
            ).Variant.Variants
        ).'Variant Name')
