                        ) / 100) * ((LookUp(
                            OfflineProductPriceCollection,
                            'Product SKU'.'Product SKU' = ThisItem.'Product SKU' && 'Price list'.Name = varPricelist.Name
                        ).'Ex-Factory') * ('Quantity Input_7'.Text)),
                        // calculate SGST--------------------------------------------
                        sgstAmount: (If(
                            varOrderfrom = varOrderTo,
                            If(
                                !IsBlank(
                                    LookUp(
                                        'HSN Masters',
                                        'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                    ).'GST %'
                                ),
                                LookUp(
                                    'HSN Masters',
                                    'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                ).'GST %' / 2,
                                LookUp(
                                    'HSN Masters',
                                    'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                ).'SGST %'
                            )
                        ) / 100) * ((LookUp(
                            OfflineProductPriceCollection,
                            'Product SKU'.'Product SKU' = ThisItem.'Product SKU' && 'Price list'.Name = varPricelist.Name
                        ).'Ex-Factory') * ('Quantity Input_7'.Text)),
                        //if state is not same then calculate IGST----------------------
                        igstAmount: (If(
                            Not(varOrderfrom = varOrderTo),
                            If(
                                !IsBlank(
                                    LookUp(
                                        'HSN Masters',
                                        'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                    ).'GST %'
                                ),
                                LookUp(
                                    'HSN Masters',
                                    'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                ).'GST %',
                                LookUp(
                                    'HSN Masters',
                                    'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                ).'IGST %'
                            )
                        ) / 100) * ((LookUp(
                            OfflineProductPriceCollection,
                            'Product SKU'.'Product SKU' = ThisItem.'Product SKU' && 'Price list'.Name = varPricelist.Name
                        ).'Ex-Factory') * ('Quantity Input_7'.Text)),
                        //Calculate cess-----------------
                        cessAmount: If(
                            IsBlank(
                                LookUp(
                                    'HSN Masters',
                                    'HSN Master' = ThisItem.'HSN '.'HSN Master'
                                ).'Cess %'
                            ),
                            0,
                            ((LookUp(
                                'HSN Masters',
                                'HSN Master' = ThisItem.'HSN '.'HSN Master'
                            ).'Cess %') / 100) * ((LookUp(
                                OfflineProductPriceCollection,
                                'Product SKU'.'Product SKU' = ThisItem.'Product SKU' && 'Price list'.Name = varPricelist.Name
                            ).'Ex-Factory') * ('Quantity Input_7'.Text))
                        )
                    }
                );
                Notify(
                    "Item added to Secondary Cart",
                    NotificationType.Success,
                    1000
                );
                ,
            //___________________________ELSE already present in Secondary cart
                Patch(
                    SecondaryCart,
                    LookUp(
                        SecondaryCart,
                        productSKU.'Product SKU' = ThisItem.'Product SKU'
                    ),
                    {quantity: 'Quantity Input_7'.Text}
                );
            );
        ),
        Notify(
            "Please enter quantity.",
            NotificationType.Warning,
            700
        )
    );
    
)

