If(
    ThisItem.'Created By'.'Primary Email' = User().Email,
    If(
        CountIf(
            Attendances,
            User.'Primary Email' = User().Email && Date = Today() && !IsBlank('Check-out Time')
        ) > 0,
        Notify(
            "Your day has Ended, you can not start Visit",
            NotificationType.Error,
            2000
        ),
        If(
            CountIf(
                Attendances,
                User.'Primary Email' = User().Email && Date = Today() && !IsBlank('Check-in Time')
            ) = 0,
            Notify(
                "Kindly Start your day first",
                NotificationType.Error,
                2000
            );
            Navigate(Home),
            If(
                Not(
                    Text(
                        ThisItem.'Visit date',
                        "dd/mm/yyyy"
                    ) = Text(
                        Today(),
                        "dd/mm/yyyy"
                    )
                ),
                Notify(
                    "Visit cannot be started",
                    NotificationType.Error,
                    2000
                ),
                IsBlank(
                    LookUp(
                        OfflineVisitCollection,
                        'Visit date' = Today() && 'Created By'.'Primary Email' = User().Email && 'Visit Status' = [@'Visit Status'].Started && Not(Visit = ThisItem.Visit)
                    )
                ),
                If(
                    Connection.Connected,
                    Patch(
                        Visits,
                        LookUp(
                            Visits,
                            Visit = ThisItem.new_visitid
                        ),
                        {
                            'Visit Status': 'Visit Status'.Started,
                            'Check-in Time': Now(),
                            'Check-in Geo Location Lat': Location.Latitude,
                            'Check-in Geo Location Long': Location.Longitude,
                            'Check-in Address': LookUp(
                                Parties_1,
                                Address = ThisItem.Party.Address
                            ).Address
                        }
                    );
                    Notify(
                        "Visit has been started",
                        NotificationType.Success,
                        700
                    );
                    ,
                    Collect(
                        OfflineVisitsToBeSynced,
                        {
                            visitItem: LookUp(
                                OfflineVisitCollection,
                                Visit = ThisItem.new_visitid
                            ),
                            visitGUID: ThisItem.Visit,
                            status: 'Visit Status'.Started,
                            'Check-in Time': Now(),
                            'Check-in Geo Location Lat': Location.Latitude,
                            'Check-in Geo Location Long': Location.Longitude,
                            'Check-in Address': LookUp(
                                Parties_1,
                                Address = ThisItem.Party.Address
                            ).Address
                        }
                    );
                    Patch(
                        OfflineVisitCollection,
                        LookUp(
                            OfflineVisitCollection,
                            Visit = ThisItem.Visit
                        ),
                        {
                            'Visit Status': 'Visit Status'.Started,
                            'Check-in Time': Now(),
                            'Check-in Geo Location Lat': Location.Latitude,
                            'Check-in Geo Location Long': Location.Longitude,
                            'Check-in Address': LookUp(
                                Parties_1,
                                Address = ThisItem.Party.Address
                            ).Address
                        }
                    );
                    SaveData(
                        OfflineVisitsToBeSynced,
                        "OfflineVisitsToBeSynced"
                    );
                    Notify(
                        "Data has been saved on local",
                        NotificationType.Success,
                        1000
                    );
                );
                Navigate(
                    'Visit Info',
                    ScreenTransition.CoverRight
                );
                Set(
                    _VarClickedVisitAccount,
                    ThisItem.Party
                );
                Set(
                    varCustomerforDiscount,
                    ThisItem.Party.'Parties (new_partiesid)'
                );
                //Set attached dist according_______________________________________________________________
                If(
                    ThisItem.Party.'Customer Type' = 'Party Type Choices'.Retailer,
                    Set(
                        varSelectedRetailerAttachedDistributor,
                        LookUp(
                            OfflinePartiesCollection,
                            'Customer Code' = ThisItem.Party.'Customer Code'
                        ).'Attached Distributor'.'Parties (new_partiesid)'
                    );
                    ,
                    Set(
                        varSelectedRetailerAttachedDistributor,
                        Blank()
                    )
                );
                Set(
                    varVisitId,
                    ThisItem.Visit
                );
                ,
                Notify(
                    "One Visit is Already Started.",
                    NotificationType.Warning,
                    1000
                )
            )
        )
    );
    ,
    Notify(
        "You cannot start the subordinate visit",
        NotificationType.Error,
        2000
    )
);
ClearCollect(
    OfflineVisitCollection,
    Visits
);

