Rules.InvariantScript:
If(
    'Primary Cart'.CartType = "Primary",
    If(
        Value('Quantity Input_5'.Text) = 0,
        Remove(
            colPrimaryCart,
            LookUp(
                colPrimaryCart,
                productSKU = ThisItem.productSKU
            )
        ),
        Patch(
            colPrimaryCart,
            LookUp(
                colPrimaryCart,
                productSKU = ThisItem.productSKU
            ),
            {quantity: 'Quantity Input_5'.Text}
        );
    ),
    //Secondary CART----------------------------------------------------
    'Primary Cart'.CartType = "Secondary",
    If(
        Value('Quantity Input_5'.Text) = 0,
        Remove(
            colSecondaryCart,
            LookUp(
                colSecondaryCart,
                productSKU = ThisItem.productSKU
            )
        ),
        Patch(
            colSecondaryCart,
            LookUp(
                colSecondaryCart,
                productSKU = ThisItem.productSKU
            ),
            {quantity: 'Quantity Input_5'.Text}
        );
    )
)

"₹" & Text(
    ((ThisItem.baseUnitPrice - (ThisItem.baseUnitPrice * (ThisItem.discount / 100))) * ThisItem.quantity) + ThisItem.igstAmount + ThisItem.cgstAmount + ThisItem.sgstAmount + ThisItem.cessAmount,
    /*"[$-hi-IN]##,##,##,###",
    "hi-IN"*/  "[$-en-US]#.00"
)

Notify("Please fill the order terms",NotificationType.Information,2000);
Set(
    varTotalBill,
    RoundUp(
        Value(
            Sum(
                colPrimaryCart,
                (baseUnitPrice - (baseUnitPrice * (discount / 100))) * quantity
            ) + (Sum(
                colPrimaryCart,
                igstAmount
            ) + Sum(
                colPrimaryCart,
                cgstAmount
            ) + Sum(
                colPrimaryCart,
                sgstAmount
            ) + Sum(
                colPrimaryCart,
                cessAmount
            ))
        ),
        2
    )
);
Navigate('Primary Cart Screen',ScreenTransition.CoverRight);
