Rules.InvariantScript:
//"₹" & ThisItem.baseUnitPrice * (ThisItem.discount/100) * ThisItem.quantity
Concatenate(
    "₹",
    Text(
        ThisItem.baseUnitPrice * (ThisItem.discount / 100) * ThisItem.quantity,
       /* "[$-hi-IN]##,##,##,###",
        "hi-IN"*/  "[$-en-US]#.00"
    )
)

If(
    'Secondary Cart'.CartType = "Primary",
    If(
        Value('Quantity Input_6'.Text) = 0,
        Remove(
            colPrimaryCart,
            LookUp(
                colPrimaryCart,
                productSKU = ThisItem.productSKU
            )
        ),
        Patch(
            colPrimaryCart,
            LookUp(
                colPrimaryCart,
                productSKU = ThisItem.productSKU
            ),
            {quantity: 'Quantity Input_6'.Text}
        );
    ),
    //Secondary CART----------------------------------------------------
    'Secondary Cart'.CartType = "Secondary",
    If(
        Value('Quantity Input_6'.Text) = 0,
        Remove(
            colSecondaryCart,
            LookUp(
                colSecondaryCart,
                productSKU = ThisItem.productSKU
            )
        ),
        Patch(
            colSecondaryCart,
            LookUp(
                colSecondaryCart,
                productSKU = ThisItem.productSKU
            ),
            {quantity: 'Quantity Input_6'.Text}
        );
    )
)

"₹" & Text(((ThisItem.baseUnitPrice - (ThisItem.baseUnitPrice * (ThisItem.discount/100))) * ThisItem.quantity)+ ThisItem.igstAmount + ThisItem.cgstAmount+ ThisItem.sgstAmount+ ThisItem.cessAmount,
/*"[$-hi-IN]##,##,##,###",
                      "hi-IN"*/  "[$-en-US]#.00")

Notify(
    "Please fill the order terms",
    NotificationType.Information,
    2000
);
Set(
    varTotalBill,
    RoundUp(
        Value(
            Sum(
                colSecondaryCart,
                (baseUnitPrice - (baseUnitPrice * (discount / 100))) * quantity
            ) + (Sum(
                colSecondaryCart,
                igstAmount
            ) + Sum(
                colSecondaryCart,
                cgstAmount
            ) + Sum(
                colSecondaryCart,
                sgstAmount
            ) + Sum(
                colSecondaryCart,
                cessAmount
            ))
        ),
        2
    )
);
Navigate(
    'Secondary Cart Screen',
    ScreenTransition.CoverRight
);
