Rules.InvariantScript:
Navigate(
    'Visit Info',
    ScreenTransition.CoverRight
);
Set(
    dataVisit,
    ThisItem
);
Set(
        varParty,
       ThisItem.Party.'Customer Name'
    );
    Set(
        varParties,
        false
    );
    Set(
        varCustomerforDiscount,
        ThisItem.Party.Parties
    );
   Set(
        varCustomertype,
         ThisItem.Party.'Customer Type'
    );
    Set(
        varpaymentterm,
        LookUp(
        OfflinePartiesCollection,
        'Customer Code' =  ThisItem.Party.'Customer Code'
    ).'Payment Terms'.Name
    );
Set(
    varOrderfrom,
    LookUp(
        OfflinePartiesCollection,
        'Customer Code' =  ThisItem.Party.'Customer Code'
    ).State.Name
);
Set(
    varOrderfromRegion,
    LookUp(
        OfflinePartiesCollection,
        'Customer Code' =  ThisItem.Party.'Customer Code'
    ).Region.Name
);
/*Set(
    varOrderTo,
    LookUp(
        colZoximaParty,
        'Customer Code' = First(colZoximaParty).'Customer Code'
    ).State.'State Name'
);*/

Set(
    varPartyPricelist,
     LookUp(
        OfflinePartiesCollection,
        'Customer Code' =  ThisItem.Party.'Customer Code'
    ).'Price List'.Name
);
ClearCollect(
    colPartyOrder,
    Sort(
        Filter(
            OfflineOrderHeaderCollection,
            'Order From'.Parties =  ThisItem.Party.Parties
        ),
        'Created On',
        SortOrder.Descending
    )
);
ForAll(
    colPartyOrder As oneline,
    Collect(
        colOrderlineproduct,
        Filter(
            'Order Lines.S',
            'Order Header'.'Order Header' = oneline.'Order Header'
        )
    )
);
ClearCollect(
    colDistinctProduct,
    Distinct(
        colOrderlineproduct,
        'Product SKU'.'Product SKU'
    )
);
ClearCollect(
    colCustomerMapping,
    LookUp(
        OfflineProductOfferMappingCollection,
        Customer.Parties = GUID(varCustomerforDiscount)
    )
);
ClearCollect(
    colStateMapping,
    LookUp(
        OfflineProductOfferMappingCollection,
        Customer.State.Name = varOrderfrom
    )
);
ClearCollect(
    colRegionMapping,
    LookUp(
        OfflineProductOfferMappingCollection,
        Customer.Region.Name = varOrderfromRegion
    )
);
If(
    !IsBlank(colCustomerMapping),
    ClearCollect(
        colOfferHeaderId,
        LookUp(
            OfflineProductOfferHeaderCollection,
            'Product Offer Header' = First(colCustomerMapping).'Product Offers'.'Product Offer Header'
        ).'Offer ID'
    ),
    !IsBlank(colStateMapping),
    ClearCollect(
        colOfferHeaderId,
        LookUp(
            OfflineProductOfferHeaderCollection,
            'Product Offer Header' = First(colStateMapping).'Product Offers'.'Product Offer Header'
        ).'Offer ID'
    ),
    !IsBlank(colRegionMapping),
    ClearCollect(
        colOfferHeaderId,
        LookUp(
            OfflineProductOfferHeaderCollection,
            'Product Offer Header' = First(colStateMapping).'Product Offers'.'Product Offer Header'
        ).'Offer ID'
    )
);
ClearCollect(
    colOfferHeader,
    Filter(
        OfflineProductOfferHeaderCollection,
        'Offer ID' = First(colOfferHeaderId).Value && ('Active From' <= Today()) && ('Active To' >= Today())
    )
);

/*If(
    LookUp(
        OfflineVisitCollection,
        zx_visitid = ThisItem.zx_visitid
    ).zx_visitstatus = 'Visit Status'.Completed,
    DisplayMode.Disabled,
    DisplayMode.Edit
)*/

If(
    ThisItem.'Visit Status' = [@'Visit Status'].Completed,
    Notify(
        "Your Visit is Already Ended",
        NotificationType.Success,1000
    ),
    If(
        Self.Image = 'play (1)',
        (If(
            ThisItem.createdby.'Primary Email' = User().Email,
            If(
                CountIf(
                    'Attendance.S',
                    User.'Primary Email' = User().Email && Date = Today() && !IsBlank('Check-out Time')
                ) > 0,
                Notify(
                    "Your day has Ended, you can not start Visit",
                    NotificationType.Error,
                    2000
                ),
                If(
                    CountIf(
                        'Attendance.S',
                        User.'Primary Email' = User().Email && Date = Today() && !IsBlank('Check-in Time')
                    ) = 0,
                    Notify(
                        "Kindly Start your day first",
                        NotificationType.Error,
                        2000
                    );
                    Navigate(Home),
                    If(
                        Not(
                            Text(
                                ThisItem.zx_visitdate,
                                "dd/mm/yyyy"
                            ) = Text(
                                Today(),
                                "dd/mm/yyyy"
                            )
                        ),
                        Notify(
                            "Visit cannot be started",
                            NotificationType.Error,
                            2000
                        ),
                        IsBlank(
                            LookUp(
                                OfflineVisitCollection,
                                zx_visitdate = Today() && createdby.'Primary Email' = User().Email && zx_visitstatus = [@'Visit Status'].Started && Not(zx_visitid = ThisItem.zx_visitid)
                            )
                        ),
                        If(
                            Connection.Connected,
                            Patch(
                                'Visit.S',
                                LookUp(
                                    'Visit.S',
                                    Visit = ThisItem.zx_visitid
                                ),
                                {
                                    'Visit Status': 'Visit Status'.Started,
                                    'Check-in Time': Now(),
                                    'Check-in Geo Location Lat': Location.Latitude,
                                    'Check-in Geo Location Long': Location.Longitude,
                                    'Check-in Address': LookUp(
                                        'Parties.S',
                                        Address = ThisItem.zx_Party.Address
                                    ).Address
                                }
                            );
                            Notify(
                                "Visit has been started",
                                NotificationType.Success,
                                700
                            );
                            ,
                            Collect(
                                OfflineVisitsToBeSynced,
                                {
                                    visitItem: LookUp(
                                        OfflineVisitCollection,
                                        zx_visitid = ThisItem.zx_visitid
                                    ),
                                    visitGUID: ThisItem.zx_visitid,
                                    status: 'Visit Status'.Started,
                                    'Check-in Time': Now(),
                                    'Check-in Geo Location Lat': Location.Latitude,
                                    'Check-in Geo Location Long': Location.Longitude,
                                    'Check-in Address': LookUp(
                                        'Parties.S',
                                        Address = ThisItem.zx_Party.Address
                                    ).Address
                                }
                            );
                            Patch(
                                OfflineVisitCollection,
                                LookUp(
                                    OfflineVisitCollection,
                                    zx_visitid = ThisItem.zx_visitid
                                ),
                                {
                                    zx_visitstatus: 'Visit Status'.Started,
                                    zx_checkintime: Now(),
                                    zx_checkingeolocationlat: Location.Latitude,
                                    zx_checkingeolocationlong: Location.Longitude,
                                    zx_checkinaddress: LookUp(
                                        'Parties.S',
                                        Address = ThisItem.zx_Party.Address
                                    ).Address
                                }
                            );
                            SaveData(
                                OfflineVisitsToBeSynced,
                                "OfflineVisitsToBeSynced"
                            );
                            Notify(
                                "Data has been saved on local",
                                NotificationType.Success,
                                1000
                            );
                        );
                        Set(
                            _VarActivetopTabID,
                            1
                        );
                        Navigate(
                            'Visit Info',
                            ScreenTransition.CoverRight
                        );
                        Set(
                            dataVisit,
                            ThisItem
                        );
                        Set(
                            _varClickedVisitAccount,
                            ThisItem.zx_Party.Parties
                        );
                        Set(
                            _varCustomerforDiscount,
                            ThisItem.zx_Party.Parties
                        );
                //Set attached dist according_______________________________________________________________
If(
                            ThisItem.zx_Party.'Customer Type' = 'Party Type Choices'.Retailer,
                            Set(
                                _varSelectedRetailerAttachedDistributor,
                                LookUp(
                                    OfflinePartiesCollection,
                                    zx_customercode = ThisItem.zx_Party.'Customer Code'
                                ).zx_AttachedDistributor.Parties
                            );
                            ,
                            Set(
                                _varSelectedRetailerAttachedDistributor,
                                Blank()
                            )
                        );
                        Set(
                            _varVisitId,
                            ThisItem.zx_visitid
                        );
                        ,
                        Notify(
                            "Visit is Already Started.",
                            NotificationType.Warning,
                            1000
                        )
                    )
                )
            );
            ,
            Notify(
                "You cannot start the subordinate visit",
                NotificationType.Error,
                2000
            )
        ));
        ,
    // changing offline visit collection to table i.e OfflineVisitCollection to visit.s
        Patch(
            'Visit.S',
            LookUp(
                'Visit.S',
                'Visit ID' = ThisItem.'Visit ID'
            //zx_visitid = ThisItem.zx_visitid
            ),
            {
                zx_visitstatus: 'Visit Status'.Completed,
                zx_checkoutaddress: Concatenate(
                    Location.Latitude,
                    ",",
                    Location.Longitude
                ),
                zx_checkouttime: Now()
            }
        );
        Notify(
            "Your visit has been successfully ended.",
            NotificationType.Success,
            2500
        );
    );
    ClearCollect(
        OfflineVisitCollection,
        'Visit.S'
    );
    
);
Refresh('Parties.S');
Refresh('Visit.S')

