If(
    ThisItem.'Created By'.'Primary Email' = User().Email,
    If(
        CountIf(
            Attendances,
            User.'Primary Email' = User().Email && Date = Today() && !IsBlank('Check-out Time')
        ) > 0,
        Notify(
            "Your day has Ended, you can not start Visit",
